import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component{

  constructor(){
    super();
    this.state={
      name:{firstname:'Yaksh',lastname:'Thakkar'},
      company:'RBC'

    }
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi {this.state.name.firstname} {this.state.name.lastname}, I work at {this.state.company}
          </p>
          <button onClick={()=>{
            this.setState(
              ()=>{
                return {
                  name:{firstname:'Sanket',lastname:'Mehta'},
                }
              }
          );}}>Change name</button>
        </header>
      </div>
    );
  }

}

export default App;
